mapper.insert:
insert
	into tb_member
	(
	    mem_no
	  ,	mem_id
	  , mem_name
	  , mem_pwd
	  , mem_email
	  , mem_nickname
	  , gender
	)
	values
	(
	   seq_memno.nextval
	 , #{memId}
	 , #{memName}
	 , #{memPwd}
	 , #{email}
	 , #{memNickname}
	 , #{gender}
	)

mapper.update:
update
	tb_member
 set mem_pwd = #{memPwd}
 where mem_id = #{memId}
  and mem_email = #{email}
  and mem_name = #{memName}

update
	tb_member
set mem_mbti = #{mbti}
where mem_id = #{memId}

	update
		tb_member
    set mem_nickname = #{memNickname}
  where mem_id = #{memId}
